mapper.insert:

	select 'ORD' || LPAD(order_code_seq.NEXTVAL, 7, '0') as orderCode from dual

	insert into orders (order_code , bp_code , order_date , order_manager , dod_date , prod_plan_yn )
	values(
		#{orderCode}
		,#{bpCode}
		,#{orderDate}
		,#{orderManager}
		,#{dodDate}
		,#{prodPlanYn}
	)

mapper.select:
select bp_code , bp_name , bp_main, get_common(bp_type, '005') bp_type from business_partner
bp_type = 'BP02'
and bp_code = #{bpCode}

mapper.update:
  			update fin_prdt 
			   set prdt_cnt = prdt_cnt - #{prdtOutCnt}
			 where prdt_lot_no = #{prdtLotNo}

update orders
   set dod_state = '출고완료'
 where order_code = #{orderCode}

  	 update prdt_chk
		set prdt_fin_chk = '입고완료'
		where prdt_chk_code = #{prdtChkCode}

  		 update prdt_chk
			set prdt_fin_chk = '입고전'
		  where prdt_chk_code = #{prdtChkCode}
