mapper.select:
select EQM_CODE ,EQM_NAME ,case when EQM_UCHECK='Y' then '사용' else '미사용' end EQM_UCHECK ,EQM_MAX_TEMP ,EQM_MIN_TEMP ,EQM_MANAGER ,EQM_DIVISION ,INSP_CYCLE ,MFG_COMP ,MFG_DATE ,EQM_IMG , EQM_STATE , ( select max(RECENT_INSP_DATE) from eqm_insp i WHERE e.eqm_code = i.eqm_code ) as RECENT_INSP_DATE, ( select max(NEXT_INSP_DATE) from eqm_insp i WHERE e.eqm_code = i.eqm_code ) as NEXT_INSP_DATE from eqm e
e.eqm_ucheck = #{eqmUcheck}
ORDER BY eqm_code

mapper.insert:

		select 'eqm' || LPAD(eqm_code_seq.NEXTVAL, 7, '0') as eqmCode from dual

	INSERT INTO EQM
			 ( 
			    EQM_CODE
			   ,EQM_NAME
			   ,EQM_UCHECK
		  	   ,EQM_MIN_TEMP
			   ,EQM_MAX_TEMP
			   ,EQM_MANAGER
			   ,INSP_CYCLE
			   ,MFG_COMP
			   ,MFG_DATE
			   ,EQM_IMG
			   ,EQM_DIVISION
			)
 values
         ( 
 				#{eqmCode}
 			   ,#{eqmName}
 			   ,#{eqmUcheck}
 			   ,#{eqmMinTemp}
 			   ,#{eqmMaxTemp}
 			   ,#{eqmManager}
 			   ,#{inspCycle}
 			   ,#{mfgComp}
 			   ,#{mfgDate}   
 			   ,#{eqmImg}
 			   ,#{eqmDivision}

         )
