mapper.select:
SELECT e.eqm_code,e.eqm_name,e.eqm_ucheck,e.insp_cycle,e.mfg_comp,e.mfg_date,i.RECENT_INSP_DATE,i.NEXT_INSP_DATE FROM eqm e left join EQM_INSP i ON e.eqm_code = i.eqm_code
eqm_ucheck = #{eqmUcheck}
ORDER BY eqm_code

select eqm_code,eqm_name,eqm_ucheck,insp_cycle,eqm_min_temp,eqm_max_temp,eqm_manager,mfg_comp,mfg_date,eqm_img
from EQM
where eqm_code=#{eqmCode} and eqm_name = #{eqmName} and  mfg_comp = #{mfgComp}

mapper.insert:

		select 'eqm' || LPAD(eqm_code_seq.NEXTVAL, 7, '0') as eqmCode from dual

	INSERT INTO EQM
			 ( 
			    EQM_CODE
			   ,EQM_NAME
			   ,EQM_UCHECK
		  	   ,EQM_MIN_TEMP
			   ,EQM_MAX_TEMP
			   ,EQM_MANAGER
			   ,INSP_CYCLE
			   ,MFG_COMP
			   ,MFG_DATE
			   ,EQM_IMG
			)
 values
         ( 
 				#{eqmCode}
 			   ,#{eqmName}
 			   ,#{eqmUcheck}
 			   ,#{eqmMinTemp}
 			   ,#{eqmMaxTemp}
 			   ,#{eqmManager}
 			   ,#{inspCycle}
 			   ,#{mfgComp}
 			   ,#{mfgDate}   
 			   ,#{eqmImg} 
         )
