mapper.delete:
DELETE code_detail
WHERE code_id = #{codeId}
AND code_type = #{codeType}

mapper.insert:

SELECT #{empDept} || NVL( LPAD( SUBSTR( MAX(emp_id), 5) +1, 4, '0'), '0001') from emp_list
WHERE emp_dept = #{empDept}			

INSERT INTO emp_list(emp_id, emp_name, emp_dept, emp_class, emp_phone)
values(
		#{empId}
		, #{empName}
		, #{empDept}
		, #{empClass}
		, #{empPhone}
		)
