mapper.select:
SELECT mtl_date, mtl_code, bp_code, mtl_name, mtl_manager, mtl_cnt FROM mtl_need
mtl_where = 'MT01'
AND mtl_date >= #{mtlDate} AND mtl_date < #{mtlDate}+1
AND bp_code = #{bpCode}
AND mtl_code = #{mtlCode}
AND mtl_manager LIKE '%' || #{mtlManager} ||'%'

mapper.update:
  		UPDATE mtl_chk
  		SET mtl_chk_cnt = #{mtlChkCnt},
  			mtl_error_cnt = #{mtlErrorCnt},
  			mtl_pass_cnt = #{mtlPassCnt}


	       mtl_chk_code = #{mtlChkCode}
