mapper.update:
update dt_prod_plan
set prod_plan_cnt = #{prodPlanCnt},
    prod_rank = #{prodRank},
    prod_plan_note = #{prodPlanNote}
where dt_prod_plan_code = #{dtProdPlanCode}

mapper.select:
select p.prod_plan_code , pt.prdt_code , pt.prdt_name , pt.prdt_type 
from prdt_table pt join dt_prod_plan p
                     on pt.prdt_code = p.prdt_code


		pt.prdt_code = #{prdtCode}


		AND p.prod_plan_code = #{prodPlanCode}

mapper.insert:

    select 'PIN' || LPAD(prod_inst_seq.NEXTVAL, 7, '0') as prodInstCode from dual

{call in_inst_up_plan(
	#{prodInstCode},
	#{prodPlanCode},
    #{prodInstName},
    #{prodInstManager},
   	#{prodInstDate},
    #{prodInstSdate},
    #{prodInstEdate}
)}

mapper.update:
UPDATE	process
SET 		prdt_total = #{prodInstCnt}
WHERE 	dt_prod_inst_code = #{dtProdInstCode}
AND 		prcs_order = 1

update dt_prod_inst
set prod_inst_rank = #{prodInstRank},
    prod_inst_cnt = #{prodInstCnt}
where dt_prod_inst_code = #{dtProdInstCode}

mapper.select:
select prcs_type, prcs_std_code, eqm_code, eqm_name, eqm_state
from prcs_table JOIN bom_detail using(prcs_std_code)
                JOIN bom_table using(bom_code)
                JOIN prdt_table using(prdt_code)
                JOIN eqm ON eqm.eqm_division = prcs_table.prcs_type
where bom_use = 'Y'
AND   prcs_order = #{prcsOrder}
AND   prdt_code = #{prdtCode}
AND   eqm_ucheck = 'Y'

mapper.insert:

        	select 'PRCR' || LPAD(PROCESS_RESULT_SEQ.NEXTVAL, 6, '0') as prcsRsCode from dual

		insert into process_result( PRCS_RS_CODE , PRDT_CODE , PRCS_STD_CODE , EQM_CODE , PRCS_CODE , PRDT_CNT , MANAGER)
		values(
				#{prcsRsCode}
			   ,#{prdtCode}
			   ,#{prcsStdCode}
			   ,#{eqmCode}
			   ,#{prcsCode}
			   ,#{prdtCnt}
			   ,#{manager}

				)

mapper.update:
update eqm
   set  EQM_STATE = 'Y'
 where eqm_code = #{eqmCode}

mapper.insert:

SELECT LPAD(NVL(max(holding_code)+1,1),10,'0') FROM holding

INSERT INTO holding(holding_code
					, prod_inst_code
					, mtl_lot_num
					, holding_cnt)
VALUES(#{holdingCode}
		 ,	#{prodInstCode}
		 , #{mtlLotNum}
		 ,	#{holdingCnt})

mapper.select:
SELECT h.*, m.mtl_code
FROM holding h JOIN mtl_container m ON h.mtl_lot_num = m.mtl_lot_num
WHERE prod_inst_code = #{prodInstCode}

mapper.update:
UPDATE prod_plan
SET prod_inst_yn = 'n'
WHERE prod_plan_code = #{prodPlanCode}
